documentChange(commands, params){
  console.log('documentChange',params,this.documents)
  if(params.widgetId)controller.documentId = params.widgetId;
  // let index = this.documents.findIndex((item)=>item.documentId==params.widgetId)
  this.documents.forEach((item,index_)=>{
    if(item.documentId == params.widgetId){
      document.querySelector('.page-num').innerHTML = `${this.documents.length}/${item.documentNo}`
      if(this.documents.length==item.documentNo) document.querySelector('.page-right').innerHTML= '+'
      else{document.querySelector('.page-right').innerHTML= '>'}
      document.querySelectorAll('.page-item').forEach((item)=>{
        if(Number(item.getAttribute('index')) == index_){
          item.style.backgroundColor = 'rgba(79,193,176,.15)'
          document.querySelector('.page-add').style.backgroundColor = '#FFFFFF'
        }else{
          item.style.backgroundColor = '#FFFFFF'
        }
      })
      return
    }
  })
}
