function $jq(div){return document.querySelector(div)}
function  toggle(ele,num,oneself,type) {
  if(type){
    GlobalMethod.pen_selected_type = type
    GlobalMethod.switch_isShow(GlobalMethod.pen_selected_type)
    
  }
  let Elediv = document.querySelector(ele);
  for(let i in Elediv.children) {
   if(num == i){
    if(oneself && Elediv.children[i].style.display == 'flex'){
      Elediv.children[i].style.display = 'none'
    }else{
      Elediv.children[i].style.display = 'flex'
    }
   }else{
    if(Number(i)|| Number(i) == 0)  {Elediv.children[i].style.display = 'none'}
   }
  }
}
function hide(ele) {
  let elechildren 
  for(let i in ele.children) {
    if(ele.children[i].className && ele.children[i].className.includes('content')) {
      elechildren = ele.children[i]
    }
  }
  if(elechildren.style.display == 'block')  elechildren.style.display='none';
  else elechildren.style.display='block';
}
function show(ele,off) {
  let elechildren 
  for(let i in ele.children) {
    if(ele.children[i].className == 'dome-content') {
      elechildren = ele.children[i]
    }
  }
  if(elechildren.style.display == 'block')  elechildren.style.display='none';
  else {
    document.body.onclick && !off && document.body.onclick()
    elechildren.style.display='block'
  };
  event && event.stopPropagation && event.stopPropagation()
  bodyhide(elechildren)
}
function bodyhide (divChildren,type) {
  document.body.onclick = function(){
    if( type == 'drawer') {
     $jq('.drawer').style.left='-350px'
      // divChildren.className ='moveOut drawer'
      let time = setTimeout(() =>{
       $jq('.drawer').style.cssText = 'display: none'
        clearTimeout(time)
      },500)
    } else divChildren.style.display = 'none';
   
    document.body.onclick = null;　
  }
  divChildren.onclick = function(event){
    event && event.stopPropagation && event.stopPropagation()
    
  }
}
function drawer(event) {
  
  let time
  if($jq('.drawer').style.display == 'block') {
    if(time) return clearTimeout(time)
   $jq('.drawer').style.left='-350px'
    time = setTimeout(() =>{
     $jq('.drawer').style.cssText = 'display: none'
      clearTimeout(time)
    },500)
  }
  else{
    document.body.onclick && document.body.onclick()
   $jq('.drawer').className =  'moveOut drawer'
   $jq('.drawer').style.display = 'block'

  
  };
  event && event.stopPropagation && event.stopPropagation()
  bodyhide($jq('.drawer'),'drawer')
//  GlobalMethod.createPage(true)
}
